import React, { Component } from 'react'
import './App.css'

const Book = (props) => {

    moveBookShelf = (event) => {
        console.log(event);
    }

    return (
        <div className="book">
            <div className="book-top">
                <div className="book-cover" style={{ width: 128, height: 193, backgroundImage: `url("${props.book && props.book.imageLinks && props.book.imageLinks.thumbnail}")`, backgroundRepeat: 'no-repeat' }}></div>
                <div className="book-shelf-changer">
                    <select onChange={this.moveBookShelf}>
                        <option value="move" disabled>Move to...</option>
                        <option value="currentlyReading">{props.book.shelf==='currentlyReading' && '✔ '}Currently Reading</option>
                        <option value="wantToRead">{props.book.shelf==='wantToRead' && '✔ '}Want to Read</option>
                        <option value="read">{props.book.shelf==='read' && '✔ '}Read</option>
                        <option value="none">{props.book.shelf==='none' && '✔ '}None</option>
                    </select>
                </div>
            </div>
            <div className="book-title">{props.book.title}</div>
            <div className="book-authors">{props.book.authors}</div>
        </div>
    )
}

export default Book;
